const GlobalStyles = props => (
  <style jsx global>{`
        h1, h2, h3, h4, h5, h6, p, a, ul, ol, li, small, span, div, body, html {
          font-family: Helvetica, Arial; 
        }
        .menu-items a {
          listStyle: none;
          padding: 6px 0;
          color: #333;
        }
        a {
          text-decoration: none;
        }

        .markdown a:hover {
          opacity: 0.6;
        }

        .markdown h3 {
          margin: 0;
          padding: 0;
          text-transform: uppercase;
        }
      `}</style>
);


export default GlobalStyles;
