import Header from './Header';
import { ThemeProvider } from 'theme-ui'
import theme from '../theme'
import { Flex, Box } from 'rebass'
import GlobalStyles from './GlobalStyles'


const Layout = props => (
  <ThemeProvider theme={theme}>
    <Flex
      sx={{
        height: '100vh'
      }}
    >
      <Box
        p={4}
        width={[0, 1/6]}
        bg=''>
        <Header />
      </Box>
      <Box
        p={4}
        width={[1, 5/6]}
        bg=''
        sx={{
          overflow: 'auto'
        }}
      >
        {props.children}
      </Box>
    </Flex>
    <GlobalStyles />
  </ThemeProvider>
);

export default Layout;
